import React, { useEffect, useRef, useState } from 'react';

interface MapProps {
  googleMapsApiKey: string;
}

const MapComponent: React.FC<MapProps> = ({ googleMapsApiKey }) => {
  const [map, setMap] = useState<google.maps.Map | null>(null);
  const setUserLocation = (location: google.maps.LatLng | null) => {
    if (location) {
      map?.setCenter(location);
      new google.maps.Marker({
        position: location,
        map: map,
        title: 'Your Location',
      });
    }
  };
  const mapRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const loadMap = () => {
      if (mapRef.current) {
        const newMap = new google.maps.Map(mapRef.current, {
          center: { lat: 0, lng: 0 },
          zoom: 15,
        });
        setMap(newMap);
      }
    };

    if (!map && googleMapsApiKey) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=${googleMapsApiKey}&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = loadMap;
      document.head.appendChild(script);
    }

    return () => {
      const scriptToRemove = document.querySelector(`script[src^="https://maps.googleapis.com/maps/api/js"]`);
      if (scriptToRemove) {
        document.head.removeChild(scriptToRemove);
      }
    };
  }, [googleMapsApiKey, map]);

  useEffect(() => {
    const getCurrentLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const userLatLng = new google.maps.LatLng(position.coords.latitude, position.coords.longitude);
            setUserLocation(userLatLng);
            map?.setCenter(userLatLng);

            new google.maps.Marker({
              position: userLatLng,
              map: map,
              title: 'Your Location',
            });
          },
          (error) => {
            console.error('Error getting location:', error);
          }
        );
      } else {
        console.error('Geolocation is not supported by this browser.');
      }
    };

    if (map) {
      getCurrentLocation();
    }
  }, [map]);

  return <div ref={mapRef} style={{ height: '400px', width: '100%' }} />;
};

export default MapComponent;
